import axios from "axios";
import { useState, useEffect } from 'react';
import { Card  } from 'react-bootstrap';

const GIPHY_URL = 'https://api.giphy.com/v1/gifs';
const API_KEY = 'ld7uR2ZapD9Ae4pzCZFJrBRyF1FMAeRW';

const GiphyViewer = () => {

    const [gifs, setGifs] = useState([]);

    useEffect(() => {
        // console.log("loaded");

        axios.get(`${GIPHY_URL}/trending?api_key=${API_KEY}`)
             .then((response) => {
                console.log(response.data.data);
                setGifs(response.data.data);
             })
             .catch((error) => {
                console.log(error);
             });


    }, []);

    const gifComponents = gifs.map((g) => {
        return (
            <div>
              <GifCard ket={g.id} title={g.title} url={g.url} image={g.images.fixed_width.url}/>
            </div>
        );
    });
    


    return (
        <div>
            {gifComponents}
        </div>
    );
}


const GifCard = (prop) => {
    return(
        <Card>
            <Card.Img />
            <Card.Body>
                <Card.Title><a href={props.url}>{props.title}</a></Card.Title>
            </Card.Body>
        </Card>
    );
};


export default GiphyViewer;
