import axios from "axios";
import { useState, useEffect } from 'react';
import { Card, Button  } from 'react-bootstrap';
import { Row  } from 'react-bootstrap';

const GIPHY_URL = 'https://api.giphy.com/v1/gifs';
const API_KEY = 'NDXI27bWKLlclzIRrWbx3QcmbWHjh173';

const GiphyViewer = () => {

    const [gifs, setGifs] = useState([]);
const [term, setTerm] = useState ([]);

    useEffect(() => {
        // console.log("loaded");

        axios.get(`${GIPHY_URL}/trending?api_key=${API_KEY}`)
             .then((response) => {
                console.log(response.data.data);
                setGifs(response.data.data);
             })
             .catch((error) => {
                console.log(error);
             });


    }, []);

const handleTrendingClick= () => {
    getTrending()
}

    const handleChange = (e) => {
        setTerm(e.target.value)
    };

    const handleClick= (e) => {
   
    };

    const handleKeyUP= (e) => {
       if(e.key === `Enter`){
        searchGif();
       }
    
    };


    const searchGif = () => {
        if(!term){
            alert("please enetr a search term")
            return;
     }
     
        axios.get(`${GIPHY_URL}/trending?api_key=${API_KEY}&q=${term}`)
                 .then((response) => {
                    console.log(response.data.data);
                    setGifs(response.data.data);
                 })
                 .catch((error) => {
                    console.log(error);
                 });
                 setTerm("")
    };

    const gifComponents = gifs.map((g) => {
        return (
            <div>
              <GifCard key={g.id} title={g.title} url={g.url} image={g.images.fixed_width.url}/>
            </div>
        );
    });
    


    return (
      <>
      <div className="search">
        <input type="text" value={term} onChange={handleChange} onKeyUp={handleKeyUP}></input>
      <Button>search</Button>
       </div>
       <Row clssName="g-4" md={3} xs={1} >
            {gifComponents}
        </Row>
        </>
    );
}


const GifCard = (prop) => {
    return(
        <Card>
            <Card.Img variant="top" src={prop.image}/>
            <Card.Body>
                <Card.Title><a href={prop.url}>{prop.title}</a></Card.Title>
            </Card.Body>
        </Card>
    );
};


export default GiphyViewer;
