import axios from "axios";

import { useState, useEffect } from "react";

import { Button, Card, Row } from "react-bootstrap";

 

const GIPHY_URL = `http://api.giphy.com/v1/gifs`;

const API_KEY = `WqCf42eGdmX0mg4iKYuvvMor68YlahOA`;

 

const GiphyViewer = () => {

  const [gifs, setGifs] = useState([]);

  const [term, setTerm] = useState("");

 

  useEffect(() => {

    axios

      .get(`${GIPHY_URL}/trending?api_key=${API_KEY}`)

      .then((response) => {

        console.log(response.data.data);

        setGifs(response.data.data);

      })

      .catch((error) => {

        console.log(error);

      });

  }, []);

 

  const handleChange = (e) => {

    setTerm(e.target.value);

  };

 

  const handleClick = () => {

 

    searchGif();

  };

 

  const handleKeyUp = (e) => {

    if (e.key === "Enter") {

      searchGif();

    }

  };

 

  const searchGif = () => {

 

    if(!term){

      alert("Please enter a search term")

      return

    }

    axios

      .get(`${GIPHY_URL}/search?api_key=${API_KEY}&q=${term}`)

      .then((response) => {

        console.log(response.data.data);

        setGifs(response.data.data);

      })

      .catch((error) => {

        console.log(error);

      });

      setTerm("");

  };

 

  const handleTrendingClick = () => {

    getTrending()

  }

 

  const getTrending = () => {

    axios

    .get(`${GIPHY_URL}/trending?api_key=${API_KEY}`)

    .then((response) => {

      console.log(response.data.data);

      setGifs(response.data.data);

    })

    .catch((error) => {

      console.log(error);

    });

  }

  const gifComponents = gifs.map((g) => {

    return (

      <GifCard

        key={g.id}

        title={g.title}

        url={g.url}

        image={g.images.fixed_width.url}

      />

    );

  });

 

  return (

    <>

      <div className="search">

        <input

          type="text"

          value={term}

          onChange={handleChange}

          onKeyUp={handleKeyUp}

        />

        <Button variant="primary" onClick={handleClick}>

          Search

        </Button>

        <Button variant="primary" onClick={handleTrendingClick}>

          Trending

        </Button>

        <Row className="g-4" md={3} xs={1}>

          {gifComponents}

        </Row>

      </div>

    </>

  );

};

 

const GifCard = (props) => {

  return (

    <Card>

      <Card.Img variant="top" src={props.image} />

      <Card.Body>

        <Card.Title>

          <a href={props.url} target="_blank">

             {props.title}

          </a>

        </Card.Title>

      </Card.Body>

    </Card>

  );

};

 

export default GiphyViewer;
